(1059, 17, 'field_trips_section2_0_content_2', '<article class=\"cmnTxttrip\">\r\n                  <h3 class=\"heading-three mb-50\"> FALL <span> 2017 </span> FIELD TRIPS</h3>\r\n                  <h4 class=\"heading-four mb-30\">TRIP 1: BE A FARMER (AGENDA):</h4>\r\n                  <ul class=\"redUl ulTwocol\">\r\n                    <li>20 Minute Introduction to the Farm Map and Permaculture Design Principles.</li>\r\n                    <li>Observe how the broilers are moved.</li>\r\n                    <li>Observe how peeps are raised in the brooder.</li>\r\n                    <li>Feel the weight of a broiler vs a layer hen and discuss why the difference.</li>\r\n                    <li>Test the grain and learn about the organic variety.</li>\r\n                    <li>Learn how the ducks move from area to area. Help collect duck eggs if not already collected.</li>\r\n                    <li>Start Chores.</li>\r\n                    <li>Observe a cow milking.</li>\r\n                    <li>Jump on the wagon to start chores.</li>\r\n                    <li>Take a pumpkin home.</li>\r\n                  </ul>\r\n                </article>'),
(1060, 17, '_field_trips_section2_0_content_2', 'field_5a4dc9953c844'),
(1061, 17, 'field_trips_section2_1_content_2', '<article class=\"cmnTxttrip\">\r\n                  <h4 class=\"heading-four mb-30\">Trip 2: Pizza Harvest (Agenda):  </h4>\r\n                  <ul class=\"redUl ulTwocol\">\r\n                    <li>Visit to the 1 acre living pizza by tractor and wagon. </li>\r\n                    <li>Grind wheat berries using a wheat grinder.</li>\r\n                    <li>Introductory Lecture on the Pizza Harvest.</li>\r\n                    <li>Grind tomatoes to make tomato juice to because pizza sauce.</li>\r\n                    <li>Interactive Garden Experience (boots or shoes that can get muddy needed in inclement weather).</li>\r\n                    <li>Harvest a pumpkin.</li>\r\n                    <li>Each \"slice\" of the pizza has a different ingredient in a pizza.  For example, there is a tomato slice, an herb slice, wheat slice, cows slice for cheese, etc.</li>\r\n                    <li>Observe a cow milking to make mozzarella cheese.</li>\r\n                  </ul>\r\n                </article>'),
